import { getAdminAssetsBuyList, deleteAdminAssetsBuy, auditData } from "./api.js";
import add from "./add.vue";
import edit from "./edit.vue";
export default {
  name: "single-window",
  components: {
    add,
    edit
  },
  data() {
    return {
      tableHeight: 500,
      lookMoreModel: false,
      selected: ["选择", "序号", "采购单号", "申请人", "元器件名称", "提交状态", "审核状态", "入库状态", "预估总价格", "采购数量", "型号", "规格", "操作"],
      modal1: false,
      openSearch: true,
      // 显示搜索
      openTip: true,
      // 显示提示
      formData: {},
      loading: true,
      // 表单加载状态
      searchForm: {
        // 搜索框初始化对象
        pageNumber: 1,
        // 当前页数
        pageSize: 15,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc",
        // 默认排序方式
        submitFlag: 1
      },
      selectList: [],
      // 多选数据
      selectCount: 0,
      // 多选计数
      selectRow: {
        name: "",
        maxMoney: 0,
        minMoney: 0,
        quantity: 0,
        unit: "",
        model: "",
        spec: "",
        remark: "",
        auditFlag: 0,
        submitFlag: 0,
        wareFlag: 0
      },
      columns: [
      // 表头
      {
        type: "selection",
        width: 60,
        title: "选择",
        align: "center",
        fixed: "left"
      }, {
        title: "序号",
        width: 85,
        align: "center",
        fixed: "left",
        sortType: true,
        render: (h, params) => {
          return h("span", params.index + (this.searchForm.pageNumber - 1) * this.searchForm.pageSize + 1);
        }
      }, {
        title: "采购单号",
        key: "id",
        minWidth: 200,
        sortable: false,
        fixed: "left",
        align: "center",
        tooltip: true
      }, {
        title: "申请人",
        key: "userName",
        minWidth: 180,
        sortable: false,
        fixed: "left",
        align: "center",
        tooltip: true
      }, {
        title: "元器件名称",
        key: "name",
        minWidth: 200,
        sortable: false,
        fixed: "left",
        align: "center",
        tooltip: true
      }, {
        title: "提交状态",
        key: "submitFlag",
        align: "center",
        minWidth: 120,
        tooltip: true,
        render: (h, params) => {
          if (params.row.submitFlag == 1) {
            return h("div", [h("span", {
              style: {
                color: "#3CB371"
              }
            }, "已提交")]);
          } else {
            return h("div", [h("span", {
              style: {
                color: "#ff9900"
              }
            }, "未提交")]);
          }
        }
      }, {
        title: "审核状态",
        key: "auditFlag",
        align: "center",
        minWidth: 120,
        tooltip: true,
        render: (h, params) => {
          if (params.row.auditFlag == 1) {
            return h("div", [h("span", {
              style: {
                color: "#000000"
              }
            }, "已审核")]);
          } else if (params.row.auditFlag == 0) {
            return h("div", [h("span", {
              style: {
                color: "#ff0000"
              }
            }, "未审核")]);
          } else {
            return h("div", [h("span", {
              style: {
                color: "#f0f000"
              }
            }, "审核退回")]);
          }
        }
      }, {
        title: "入库状态",
        key: "auditFlag",
        align: "center",
        minWidth: 120,
        tooltip: true,
        render: (h, params) => {
          if (params.row.wareFlag == 1) {
            return h("div", [h("span", {
              style: {
                color: "#000000"
              }
            }, "已入库")]);
          } else {
            return h("div", [h("span", {
              style: {
                color: "#ff0000"
              }
            }, "未入库")]);
          }
        }
      }, {
        title: "预估总价格",
        key: "money",
        minWidth: 120,
        sortable: true,
        align: "center",
        tooltip: true
      }, {
        title: "采购数量",
        key: "quantity",
        minWidth: 120,
        sortable: false,
        align: "center",
        tooltip: true,
        render: (h, params) => {
          return h("div", [params.row.quantity + " " + params.row.unit]);
        }
      }, {
        title: "型号",
        key: "model",
        minWidth: 120,
        sortable: false,
        align: "center",
        tooltip: true
      }, {
        title: "规格",
        key: "spec",
        minWidth: 180,
        align: "center",
        tooltip: true,
        sortable: false
      }, {
        title: "备注",
        key: "remark",
        minWidth: 120,
        sortable: false,
        align: "center",
        tooltip: true
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "创建者",
        key: "createBy",
        sortable: true,
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "修改时间",
        key: "updateTime",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "修改者",
        key: "updateBy",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 300,
        fixed: "right",
        render: (h, params) => {
          if (params.row.auditFlag == 0 && params.row.submitFlag == 1) {
            return h("div", [h("Button", {
              props: {
                type: "primary",
                size: "small",
                ghost: true,
                icon: "ios-create-outline"
              },
              style: {
                marginRight: "5px"
              },
              on: {
                click: () => {
                  this.audit1(params.row);
                }
              }
            }, "审核通过"), h("Button", {
              props: {
                type: "error",
                size: "small",
                ghost: true,
                icon: "md-trash"
              },
              on: {
                click: () => {
                  this.audit2(params.row);
                }
              }
            }, "审核退回")]);
          } else if (params.row.submitFlag == 1) {
            return h("div", [h("span", {
              style: {
                color: "#D2691E"
              }
            }, "已被操作")]);
          } else {
            return h("div", ["尚未提交"]);
          }
        }
      }],
      data: [],
      // 表单数据
      pageNumber: 1,
      // 当前页数
      pageSize: 10,
      // 页面大小
      total: 0,
      // 表单数据总数
      showFilterPanelFlag: false,
      auditStatus: "1",
      buyAuditModel: false
    };
  },
  methods: {
    init() {
      this.getDataList();
    },
    openBuyModelWindow() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要审核的采购单");
        return;
      }
      this.buyAuditModel = true;
    },
    buyAudit() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要审核的采购单");
        return;
      }
      var that = this;
      this.$Modal.confirm({
        title: "确认审核?",
        content: "您确认要审核所选的 " + this.selectCount + " 条采购单?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          auditData({
            ids: ids,
            status: that.auditStatus
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("审核成功");
              that.clearSelectAll();
              that.getDataList();
            }
          });
        }
      });
    },
    submited() {
      this.getDataList();
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    dbrowClick(row, index) {
      this.selectRow = row;
      this.lookMoreModel = true;
    },
    rowClassNmae(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNmaeColor";
      }
      return "";
    },
    excelData() {
      this.$refs.table.exportCsv({
        filename: "导出结果"
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      // 重新加载数据
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    getDataList() {
      this.loading = true;
      getAdminAssetsBuyList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    audit1(v) {
      var that = this;
      this.$Modal.confirm({
        title: "审核通过",
        content: "您确认要审核通过?审核通过后即可操作入库！",
        loading: true,
        onOk: () => {
          auditData({
            ids: v.id,
            status: "1"
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("通过成功");
              that.getDataList();
            }
          });
        }
      });
    },
    audit2(v) {
      var that = this;
      this.$Modal.confirm({
        title: "审核退回",
        content: "您确认要审核退回?",
        loading: true,
        onOk: () => {
          auditData({
            ids: v.id,
            status: "2"
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("退回成功");
              that.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          // 批量删除
          deleteAdminAssetsBuy({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    this.init();
    this.tableHeight = Number(window.innerHeight - 290);
    this.mycolumns = this.columns;
    let showcolumns = [];
    for (var i = 0; i < this.selected.length; i++) {
      var item = this.selected[i];
      for (var j = 0; j < this.columns.length; j++) {
        if (this.columns[j].title == item) {
          showcolumns.push(this.columns[j]);
        }
      }
    }
    this.columns = showcolumns;
  },
  watch: {
    selected: function (newcolumns) {
      let showcolumns = [];
      for (var i = 0; i < this.mycolumns.length; i++) {
        var item = this.mycolumns[i];
        if (item.title == undefined) showcolumns.push(item);else if (newcolumns.includes(item.title)) showcolumns.push(item);
      }
      this.columns = showcolumns;
    }
  }
};
